import {Component} from '@angular/core';
import {ToastrService} from "ngx-toastr";

@Component({
  selector: 'app-create-quiz',
  templateUrl: './create-quiz.component.html',
  styleUrls: ['./create-quiz.component.css']
})
export class CreateQuizComponent {

  // Quiz
  quizTitle!: string;

  // Questions
  questionType: string = "SINGLE_SELECT";
  score: string = '';
  questionTitle: string = '';

  // Answers
  answersCount: any = 1;

  answer_1: string = '';
  answer_2: string = '';
  answer_3: string = '';
  answer_4: string = '';
  correctAnswerId: string = '';

  // List Object
  questions: any = [];
  answers: any = [];

  constructor(private toastr: ToastrService) {
  }

  continueCreating(form: any) {
    this.quizTitle = form.value.title;
    this.openAddQuestion();
  }

  private openAddQuestion() {
    const form = document.getElementById('create-quiz') as HTMLFormElement;
    const form2 = document.getElementById('add-question') as HTMLFormElement;

    form.classList.remove('active-c');
    form.classList.add('hidden-c');

    form2.classList.remove('hidden-c');
    form2.classList.add('active-c');
  }

  setCorrectAnswerId(id: string) {
    this.correctAnswerId = id;
  }

  addQuestion(form: any) {
    let questionTypeForm = form.value.questionType;
    let scoreForm = form.value.score.toString();
    let title = form.value.title;
    let object = {question_type: questionTypeForm, score: scoreForm, title: title};

    if (object.question_type != questionTypeForm
      || scoreForm != object.score
      || title != object.title
    ) {
      this.toastr.error('Question cannot be created!')
    }

    this.questions.push(object);

    if (this.answersCount == 2) {
      let answer1 = form.value.answer_1;
      let answer2 = form.value.answer_2;

      this.answers.push({answer1, answer2});
    }

    if (this.answersCount == 3) {
      let answer1 = form.value.answer_1;
      let answer2 = form.value.answer_2;
      let answer3 = form.value.answer_3;
      this.answers.push({answer1, answer2, answer3})
    }

    if (this.answersCount == 4) {
      let answer1 = form.value.answer_1;
      let answer2 = form.value.answer_2;
      let answer3 = form.value.answer_3;
      let answer4 = form.value.answer_4;
      this.answers.push({answer1, answer2, answer3, answer4})
    }

    this.toastr.success('Question successfully added into Quiz');

    // reset
    this.questionType = "SINGLE_SELECT";
    this.score = '';
    this.questionTitle = '';
    this.answersCount = 1;
    this.answer_1 = '';
    this.answer_2 = '';
    this.answer_3 = '';
    this.answer_4 = '';
    this.correctAnswerId = '';

    console.log(this.questions, this.answers)
  }

  protected readonly Number = Number;
}
<section class="create-quiz">
  <div class="container">

    <form action="" #createQuiz="ngForm" id="create-quiz" class="create-quiz-form active-c">
      <h3 style="color: var(--primary-color); font-family: 'Roboto', sans-serif;">Create Quiz</h3>

      <div class="form-group mt-4">
        <label>Title</label>
        <input type="text" name="title" [(ngModel)]="quizTitle" class="form-control">
      </div>

      <div class="form-group mt-4" style="width: 100%;">
        <button class="btn btn-success" style="width: 90px" (click)="continueCreating(createQuiz)">Continue</button>
      </div>
    </form>

    <form action="" #addQuestionForm="ngForm" id="add-question" class="create-quiz-form hidden-c">
      <h3 style="color: var(--primary-color); font-family: 'Roboto', sans-serif;">Add Questions</h3>

      <div class="form-group mt-4">
        <label>Title</label>
        <input type="text" name="title" class="form-control" [(ngModel)]="questionTitle">
      </div>

      <div class="form-group mt-4">
        <label>Question Type</label>
        <select class="custom-select" name="questionType" [(ngModel)]="questionType">
          <option value="SINGLE_SELECT">Single select</option>
          <option value="MULTI_SELECT">Multi select</option>
        </select>
      </div>

      <div class="form-group mt-4">
        <label>Score</label>
        <input type="number" name="score" class="form-control" [(ngModel)]="score">
      </div>

      <div class="form-group mt-4" *ngIf="questionType=='SINGLE_SELECT'">
        <label>Answers Count</label>

        <select class="custom-select" name="answersCount" [(ngModel)]="answersCount">
          <option value='2'>2</option>
          <option value='3'>3</option>
          <option value='4'>4</option>
        </select>
      </div>

      <div *ngIf="Number(answersCount) >= 2" class="form-group mt-4">
        <label>Answers</label>
        <br>
        <ng-container *ngIf="Number(answersCount) == 2">
          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '1' ? 'lime' : ''}" type="text" name="answer_1" style="width: 800px;" class="form-control" [(ngModel)]="answer_1">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('1')">✓</button>
          </div>

          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '2' ? 'lime' : ''}" type="text" name="answer_2" style="width: 800px;" class="form-control" [(ngModel)]="answer_2">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('2')">✓</button>
          </div>
        </ng-container>

        <ng-container *ngIf="Number(answersCount) == 3">
          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '1' ? 'lime' : ''}" type="text" name="answer_1" style="width: 800px;" class="form-control" [(ngModel)]="answer_1">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('1')">✓</button>
          </div>
          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '2' ? 'lime' : ''}" type="text" name="answer_2" style="width: 800px;" class="form-control" [(ngModel)]="answer_2">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('2')">✓</button>
          </div>
          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '3' ? 'lime' : ''}" type="text" name="answer_3" style="width: 800px;" class="form-control" [(ngModel)]="answer_3">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('3')">✓</button>
          </div>
        </ng-container>

        <ng-container *ngIf="Number(answersCount) == 4">
          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '1' ? 'lime' : ''}" type="text" name="answer_1" style="width: 800px;" class="form-control" [(ngModel)]="answer_1">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('1')">✓</button>
          </div>
          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '2' ? 'lime' : ''}" type="text" name="answer_2" style="width: 800px;" class="form-control" [(ngModel)]="answer_2">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('2')">✓</button>
          </div>
          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '3' ? 'lime' : ''}" type="text" name="answer_3" style="width: 800px;" class="form-control" [(ngModel)]="answer_3">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('3')">✓</button>
          </div>
          <div style=" display: inline-flex;">
            <input [ngStyle]="{'background-color':correctAnswerId == '4' ? 'lime' : ''}" type="text" name="answer_4" style="width: 800px;" class="form-control" [(ngModel)]="answer_4">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('4')">✓</button>
          </div>
        </ng-container>

      </div>

      <div class="form-group mt-4" style="width: 100%;">
        <button class="btn btn-info" style="width: 130px" (click)="addQuestion(addQuestionForm)">Add Question</button>
        <button type="submit" class="btn btn-success" style="width: 80px; margin-left: 10px">Create</button>
      </div>
    </form>
  </div>

</section>
