import {Component, EventEmitter, OnInit, Output} from '@angular/core';
import {HttpClient, HttpHeaders, HttpParams} from "@angular/common/http";
import {AxiosService} from "../../axios.service";
import {Router} from "@angular/router";
import {HeaderComponent} from "../../layouts/header/header.component";

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  password: string = "";
  username: string = "";

  constructor(private axiosService: AxiosService, private router: Router) {
  }

  onSubmitLogin(form: any): void {
    this.username = form.value.username;
    this.password = form.value.password;

    this.axiosService.request(
      "POST",
      "/login",
      {
        username: this.username,
        password: this.password
      }
    ).then(rs => {
      if(rs.status == 200) {
        this.axiosService.setAuthToken(rs.data.token);
        window.location.href="/"
      }
    })
  }
}
<div class="login">

  <div class="login-form text-center">
    <h3 class="login-form-name" style="font-family: 'Raleway', sans-serif;">Login Form</h3>

    <form #loginForm=ngForm class="mt-4" (ngSubmit)="onSubmitLogin(loginForm)">
        <div class="form-group">
          <label style="font-family: 'Raleway', sans-serif;">Email</label>
          <input
            type="email"
            name="username"
            placeholder="Email"
            class="form-control"
            [(ngModel)]="username"
            #usernameValidation
            required
            pattern="^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$"
          />


          <div class="alert alert-danger" *ngIf="questiontitle.touched && questiontitle.invalid">
            Title field is required
          </div>
        </div>

        <div class="form-group">
          <label style="font-family: 'Raleway', sans-serif;">Password</label>
          <input type="password" name="password" placeholder="Password" class="form-control" [(ngModel)]="password">
        </div>

        <div class="form-group mt-4" style="width: 100%;">
          <button type="submit" [disabled]="loginForm.invalid" class="btn btn-success" style="width: 80px">Login</button>
        </div>
    </form>
  </div>
</div>
