import {Component, Input, OnInit, Output} from '@angular/core';
import {AxiosService} from "../../axios.service";

@Component({
  selector: 'app-quiz-card',
  templateUrl: './quiz-card.component.html',
  styleUrls: ['./quiz-card.component.css']
})
export class QuizCardComponent implements OnInit{

  quizzes: any;
  createdDateTime: any;

  constructor(private axios: AxiosService) {
  }

  ngOnInit(): void  {
    this.axios.request("GET", '/get-quizzes', {}).then(
      rs => {
        if(rs.status == 200) {
          this.quizzes = rs.data;
        }
      }
    ).catch()
  }


}
<body style="background: var(--secondary-color); height: auto">
<div class="py-5">
  <div class="container">
    <div class="row hidden-md-up">

      <div class="col-md-4 mb-2" *ngFor="let quiz of quizzes; let i = index">
        <div class="card p-3">
          <div class="card-block">
            <img src="assets/quiz.png" alt="quiz png" width="40px">
            <h4 class="card-title pt-1">{{quiz.title}}</h4>
            <h6
              class="card-subtitle text-muted pt-1">{{quiz.createdDateTime.toString().slice(0, 10).replaceAll(',', '/')}}</h6>
            <button class="btn btn-primary btn-success mt-3">Take the quiz</button>
          </div>
        </div>

        <div *ngIf="i % 6 == 0">
          <hr>
        </div>
      </div>

    </div>
  </div>
</div>

<div style="position: static; margin-top: 24vh;">ㅤ</div>
<script src="https://code.jquery.com/jquery-3.1.1.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/tether/1.4.0/js/tether.min.js"></script>
<script src="https://pingendo.com/assets/bootstrap/bootstrap-4.0.0-alpha.6.min.js"></script>
</body>
