import { Component } from '@angular/core';

@Component({
  selector: 'app-create-quiz',
  templateUrl: './create-quiz.component.html',
  styleUrls: ['./create-quiz.component.css']
})
export class CreateQuizComponent {

  title: string = "";

  createQuizButton() {
    const form = document.getElementById('create-quiz') as HTMLFormElement;
    const form2 = document.getElementById('add-question') as HTMLFormElement;

    form2.classList.remove('active-c');
    form2.classList.add('hidden-c');

    form.classList.remove('hidden-c');
    form.classList.add('active-c');
  }

  addQuestionButton() {
    const form = document.getElementById('create-quiz') as HTMLFormElement;
    const form2 = document.getElementById('add-question')  as HTMLFormElement;

    form.classList.remove('active-c');
    form.classList.add('hidden-c');

    form2.classList.remove('hidden-c');
    form2.classList.add('active-c');
  }

  continueCreating(form: any) {
    const div = document.getElementById('buttons') as HTMLDivElement;
    div.classList.add('hidden-c');

    this.title = form.value.title;
    this.addQuestionButton();
  }
}
<section class="create-quiz">
  <div class="container">

    <div class="custom-buttons" id="buttons">
      <button class="custom-btn btn-warning" (click)="createQuizButton()">Create Quiz</button>
      <button class="custom-btn btn-warning" (click)="addQuestionButton()">Add Questions</button>
    </div>

    <form action="" #createQuiz="ngForm" id="create-quiz" class="create-quiz-form active-c">
      <h3 style="color: var(--primary-color); font-family: 'Roboto', sans-serif;">Create Quiz</h3>

      <div class="form-group mt-4">
        <label>Title</label>
        <input type="text" name="title" [(ngModel)]="title" class="form-control">
      </div>

      <div class="form-group mt-4" style="width: 100%;">
        <button class="btn btn-success" style="width: 80px" (click)="continueCreating(createQuiz)">Continue</button>
      </div>
    </form>

    <form action="" #createQuiz="ngForm" id="add-question" class="create-quiz-form hidden-c">
      <h3 style="color: var(--primary-color); font-family: 'Roboto', sans-serif;">Add Question</h3>

      <div class="form-group mt-4">
        <label>Title</label>
        <input type="text" name="title" [(ngModel)]="title" class="form-control">
      </div>

      <div class="form-group mt-4" style="width: 100%;">
        <button type="submit" class="btn btn-success" style="width: 80px">Create</button>
      </div>
    </form>
  </div>

</section>
