import {Component} from '@angular/core';
import {ToastrService} from "ngx-toastr";
import {AxiosService} from "../../axios.service";

@Component({
  selector: 'app-create-quiz',
  templateUrl: './create-quiz.component.html',
  styleUrls: ['./create-quiz.component.css'],
})
export class CreateQuizComponent {

  // Quiz
  quizTitle!: string;

  // Questions
  questionType: string = "SINGLE_SELECT";
  score: string = '';
  questionTitle: string = '';

  // Answers
  answersCount: any = 1;

  answer_1: string = '';
  answer_2: string = '';
  answer_3: string = '';
  answer_4: string = '';
  answer_5: string = '';
  answer_6: string = '';
  answer_7: string = '';
  answer_8: string = '';
  answer_9: string = '';
  correctAnswerId: string = '';
  correctAnswersId: string[] = [];

  // List Object
  questions: any = [];
  answers: any = [];

  constructor(private toastr: ToastrService, private axios: AxiosService) {
  }

  continueCreating(form: any) {
    this.quizTitle = form.value.title;
    this.openAddQuestion();
  }

  private openAddQuestion() {
    const form = document.getElementById('create-quiz') as HTMLFormElement;
    const form2 = document.getElementById('add-question') as HTMLFormElement;

    form.classList.remove('active-c');
    form.classList.add('hidden-c');

    form2.classList.remove('hidden-c');
    form2.classList.add('active-c');
  }

  setCorrectAnswerId(id: string) {
    this.correctAnswerId = id;
  }

  addCorrectAnswersId(id: string) {
    this.correctAnswersId.push(id);
  }
  cleanCorrectAnswersId() {
    this.correctAnswersId = [];
  }

  deleteCorrectAnswerId() {
    this.correctAnswerId = '';
  }

  addQuestion(form: any) {
    let questionTypeForm = form.value.questionType;
    let scoreForm = String(form.value.score);
    let title = form.value.title;
    let object = {questionType: questionTypeForm, score: scoreForm, title: title};
    if (object.questionType != questionTypeForm
      || scoreForm != object.score
      || title != object.title
    ) {
      this.toastr.error('Question cannot be created!');
      return;
    }

    this.questions.push(object);

    if (this.answersCount == 2) {
      let answer1 = form.value.answer_1;
      let answer2 = form.value.answer_2;

      const objectToPush1 = {
        isCorrect: Boolean(this.correctAnswerId == '1'),
        title: answer1
      };

      const objectToPush2 = {
        isCorrect: Boolean(this.correctAnswerId == '2'),
        title: answer2
      };

      this.answers.push([objectToPush1, objectToPush2]);

      this.correctAnswerId = '';
      this.answer_1 = '';
      this.answer_2 = '';
    }

    if (this.answersCount == 3) {
      let answer3 = form.value.answer_3;
      let answer4 = form.value.answer_4;
      let answer5 = form.value.answer_5;

      const objectToPush1 = {
        isCorrect: Boolean(this.correctAnswerId == '1'),
        title: answer3
      };

      const objectToPush2 = {
        isCorrect: Boolean(this.correctAnswerId == '2'),
        title: answer4
      };

      const objectToPush3 = {
        isCorrect: Boolean(this.correctAnswerId == '3'),
        title: answer5
      };
      this.answers.push([objectToPush1, objectToPush2, objectToPush3]);

      this.correctAnswerId = '';
      this.answer_3 = '';
      this.answer_4 = '';
      this.answer_5 = '';
    }

    if (this.answersCount == 4) {
      let answer6 = form.value.answer_6;
      let answer7 = form.value.answer_7;
      let answer8 = form.value.answer_8;
      let answer9 = form.value.answer_9;

      const objectToPush1 = {
        isCorrect: Boolean(this.correctAnswerId == '1'),
        title: answer6
      };

      const objectToPush2 = {
        isCorrect: Boolean(this.correctAnswerId == '2'),
        title: answer7
      };

      const objectToPush3 = {
        isCorrect: Boolean(this.correctAnswerId == '3'),
        title: answer8
      };

      const objectToPush4 = {
        isCorrect: Boolean(this.correctAnswerId == '4'),
        title: answer9
      };
      this.answers.push([objectToPush1, objectToPush2, objectToPush3, objectToPush4]);

      this.correctAnswerId = '';
      this.answer_6 = '';
      this.answer_7 = '';
      this.answer_8 = '';
      this.answer_9 = '';
    }

    // reset
    this.questionType = "SINGLE_SELECT";
    this.score = '';
    this.questionTitle = '';
    this.answersCount = 1;
    this.answer_1 = '';
    this.answer_2 = '';
    this.answer_3 = '';
    this.answer_4 = '';
    this.correctAnswerId = '';

    this.toastr.success('Question successfully added into Quiz', 'Question', {
      titleClass: "center",
      messageClass: "center"
    });
  }

  protected readonly Number = Number;

  sendToSpring() {
    const data = {
      title: this.quizTitle,
      options: this.answers,
      questions: this.questions
    }
    this.axios.request('POST', '/create-quiz', data).then( UserDto => {
        window.location.reload();
        UserDto.status == 200 ? this.toastr.success('Quiz Successfully created', 'Quiz', {
          titleClass: "center",
          messageClass: "center"
        }) : this.toastr.error('Quiz cannot be created!');
      }
    ).catch(err => {
      this.axios.setAuthToken(null);
      window.location.href = "/login-page";
    })
  }
}
<section class="create-quiz">
  <div class="container">

    <form action="" #createQuiz="ngForm" id="create-quiz" class="create-quiz-form active-c">
      <h3 style="color: var(--primary-color); font-family: 'Roboto', sans-serif;">Create Quiz</h3>

      <div class="form-group mt-4">
        <label>Title</label>
        <input
          type="text"
          name="title"
          #quiztitle='ngModel'
          required
          [(ngModel)]="quizTitle"
          class="form-control"
          [ngClass]="{'is-invalid': quiztitle.touched && quiztitle.invalid}"
        >

        <div class="alert alert-danger" *ngIf="quiztitle.touched && quiztitle.invalid">
          Title field is required
        </div>
      </div>

      <div class="form-group mt-4" style="width: 100%;">
        <button class="btn btn-success" style="width: 90px" (click)="continueCreating(createQuiz)"
                [disabled]="quiztitle.invalid">Continue
        </button>
      </div>
    </form>

    <form action="" #addQuestionForm="ngForm" id="add-question" class="create-quiz-form hidden-c">
      <h3 style="color: var(--primary-color); font-family: 'Roboto', sans-serif;">Add Questions</h3>

      <div class="form-group mt-4">
        <label>Title</label>
        <input
          type="text"
          name="title"
          class="form-control"
          [(ngModel)]="questionTitle"
          required
          #questiontitle='ngModel'
          [ngClass]="{'is-invalid': questiontitle.touched && questiontitle.invalid}"
        >

        <div class="alert alert-danger" *ngIf="questiontitle.touched && questiontitle.invalid">
          Title field is required
        </div>
      </div>

      <div class="form-group mt-4">
        <label>Question Type</label>
        <select class="custom-select" name="questionType" [(ngModel)]="questionType">
          <option value="SINGLE_SELECT">Single select</option>
          <option value="MULTI_SELECT">Multi select</option>
        </select>
      </div>

      <div class="form-group mt-4">
        <label>Score</label>
        <input
          type="number"
          name="score"
          class="form-control"
          [(ngModel)]="score"
          required
          #qScore='ngModel'
          [ngClass]="{'is-invalid': qScore.touched && qScore.invalid}"
        >

        <div class="alert alert-danger" *ngIf="qScore.touched && qScore.invalid">
          Score field is required
        </div>
      </div>

      <div class="form-group mt-4" *ngIf="questionType =='SINGLE_SELECT' || questionType == 'MULTI_SELECT'">
        <label>Answers Count</label>

        <select class="custom-select" name="answersCount" [(ngModel)]="answersCount">
          <option value='2'>2</option>
          <option value='3'>3</option>
          <option value='4'>4</option>
        </select>
      </div>

      <div *ngIf="Number(answersCount) >= 2" class="form-group mt-4">
        <label>Answers</label>
        <br>
        <button class="btn-danger btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="deleteCorrectAnswerId()">Reset correct answer</button>
        <button class="btn-danger btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="cleanCorrectAnswersId()">Reset correct answer</button>
        <br>
        <ng-container *ngIf="Number(answersCount) == 2">
          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '1' || correctAnswersId.includes('1') ? 'lime' : ''}" type="text"
                   name="answer_1" style="width: 800px;" class="form-control" [(ngModel)]="answer_1">
            <button class="btn-success btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="setCorrectAnswerId('1')">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('1')">✓</button>
          </div>

          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '2' || correctAnswersId.includes('2') ? 'lime' : ''}" type="text"
                   name="answer_2" style="width: 800px;" class="form-control" [(ngModel)]="answer_2">
            <button class="btn-success btn-primary" (click)="setCorrectAnswerId('2')" *ngIf="questionType=='SINGLE_SELECT'">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('2')">✓</button>

          </div>
        </ng-container>

        <ng-container *ngIf="Number(answersCount) == 3">
          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '1' || correctAnswersId.includes('1') ? 'lime' : ''}" type="text"
                   name="answer_3" style="width: 800px;" class="form-control" [(ngModel)]="answer_3">
            <button class="btn-success btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="setCorrectAnswerId('1')">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('1')">✓</button>

          </div>
          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '2' || correctAnswersId.includes('2') ? 'lime' : ''}" type="text"
                   name="answer_4" style="width: 800px;" class="form-control" [(ngModel)]="answer_4">
            <button class="btn-success btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="setCorrectAnswerId('2')">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('2')">✓</button>
          </div>
          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '3' || correctAnswersId.includes('3')  ? 'lime' : ''}" type="text"
                   name="answer_5" style="width: 800px;" class="form-control" [(ngModel)]="answer_5">
            <button class="btn-success btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="setCorrectAnswerId('3')">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('3')">✓</button>

          </div>
        </ng-container>

        <ng-container *ngIf="Number(answersCount) == 4">
          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '1' || correctAnswersId.includes('1')  ? 'lime' : ''}" type="text"
                   name="answer_6" style="width: 800px;" class="form-control" [(ngModel)]="answer_6">
            <button class="btn-success btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="setCorrectAnswerId('1')">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('1')">✓</button>

          </div>
          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '2' || correctAnswersId.includes('2')  ? 'lime' : ''}" type="text"
                   name="answer_7" style="width: 800px;" class="form-control" [(ngModel)]="answer_7">
            <button class="btn-success btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="setCorrectAnswerId('2')">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('2')">✓</button>
          </div>
          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '3' || correctAnswersId.includes('2')  ? 'lime' : ''}" type="text"
                   name="answer_8" style="width: 800px;" class="form-control" [(ngModel)]="answer_8">
            <button class="btn-success btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="setCorrectAnswerId('3')">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('3')">✓</button>

          </div>
          <div style=" display: inline-flex;">
            <input required [ngStyle]="{'background-color':correctAnswerId == '4' || correctAnswersId.includes('4')  ? 'lime' : ''}" type="text"
                   name="answer_9" style="width: 800px;" class="form-control" [(ngModel)]="answer_9">
            <button class="btn-success btn-primary" *ngIf="questionType=='SINGLE_SELECT'" (click)="setCorrectAnswerId('4')">✓</button>
            <button class="btn-success btn-primary" *ngIf="questionType=='MULTI_SELECT'" (click)="addCorrectAnswersId('4')">✓</button>

          </div>
        </ng-container>

      </div>

      <div class="form-group mt-4" style="width: 100%;">
        <button type="button" [disabled]="addQuestionForm.invalid || correctAnswerId=='' || correctAnswersId.length == 0" class="btn btn-info"
                style="width: 130px" (click)="addQuestion(addQuestionForm)">Add Question
        </button>
        <button type="submit" class="btn btn-success" style="width: 80px; margin-left: 10px" (click)="sendToSpring()">
          Create
        </button>
      </div>
    </form>
  </div>

</section>
