import { Component } from '@angular/core';
import {AxiosService} from "../../axios.service";
import {Router} from "@angular/router";

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {
  name: string = "";
  surname: string = "";
  username: string = "";
  password: string = "";
  type: string = "";

  constructor(private axiosService: AxiosService, private router: Router) {
  }

  onSubmitRegister(form: any) : void{
    this.name = form.value.name;
    this.surname = form.value.surname;
    this.username = form.value.username;
    this.password = form.value.password;
    this.type = form.value.type;

    this.axiosService.request(
      "POST",
      "/register",
      {
        name: this.name,
        surname: this.surname,
        username: this.username,
        password: this.password,
        type: this.type
      }
    ).then(rs => {
      if(rs.status == 201) {
        this.axiosService.setAuthToken(rs.data.token);
        this.router.navigate(['/']);
      }
    })
  }
}
<div class="register">

  <div class="register-form text-center">
    <h3 class="register-form-name" style="font-family: 'Raleway', sans-serif;">Login Form</h3>

    <form #register="ngForm" (ngSubmit)="onSubmitRegister(register)" class="mt-4">
      <div class="form-group">
        <label style="font-family: 'Raleway', sans-serif;">Name</label>
        <input type="text" name="name" placeholder="Name" class="form-control" [(ngModel)]="name">
      </div>

      <div class="form-group">
        <label style="font-family: 'Raleway', sans-serif;">Surname</label>
        <input type="text" name="surname" placeholder="Surname" class="form-control" [(ngModel)]="surname">
      </div>

      <div class="form-group">
        <label style="font-family: 'Raleway', sans-serif;">Username (Email)</label>
        <input type="email" name="username" placeholder="Email" class="form-control" [(ngModel)]="username">
      </div>

      <div class="form-group">
        <label style="font-family: 'Raleway', sans-serif;">Password</label>
        <input type="password" name="password" placeholder="Password" class="form-control" [(ngModel)]="password">
      </div>

      <div class="form-group">
        <label style="font-family: 'Raleway', sans-serif;">User Type</label>
        <select class="custom-select" name="type" [(ngModel)]="type">
          <option value="TEACHER">Teacher</option>
          <option value="STUDENT">Student</option>
        </select>
      </div>

      <div class="form-group mt-4" style="width: 100%;">
        <button type="submit" class="btn btn-success" style="width: 83px">Register</button>
      </div>
    </form>
  </div>

</div>
