import {Component, OnInit} from '@angular/core';
import {Router} from "@angular/router";
import {AxiosService} from "../../axios.service";

@Component({
  selector: 'app-single-quiz',
  templateUrl: './single-quiz.component.html',
  styleUrls: ['./single-quiz.component.css']
})
export class SingleQuizComponent implements OnInit {

  id!: number;
  quizTitle!: string;
  questions!: any;
  options!: any;

  optionsResult: any;

  currentAnswer!: any[];
  answersScores!: number;

  values: string[] = [];
  answers: any = [];

  scores!: number;

  constructor(private router: Router, private axios: AxiosService) {
    let url: string = this.router.url;
    if (url.includes(';id=')) {
      let strings = url.split(';id=');
      this.id = Number(strings[1]);

      let getQuizDataRequest = {
        id: this.id
      }

      this.axios.request("POST", '/get-data-by-quiz-id', getQuizDataRequest).then(rs => {
        if (rs.status != 200) return;
        let data = rs.data;
        this.quizTitle = data.title;
        this.questions = data.questions;
        this.options = data.options;
        console.log(this.quizTitle, this.questions, this.options)
      });
    }
  }

  getAllOptionsOfQuestion(question: any): typeof this.optionsResult {
    return this.optionsResult = this.options.filter((option: any) => {
      return option.question.id === question.id;
    });
  }

  addAnswer(id: number) {
    this.answers.push(this.values);
    this.currentAnswer = this.values;
    this.values = [];
    console.log(this.answers);

    let elementById = document.getElementById(`${id}`) as HTMLButtonElement;
    elementById.setAttribute('disabled', '')
  }

  onChangeRadio(e: any, id: number) {
    let elementById = document.getElementById(`${id}`) as HTMLButtonElement;
    if (elementById.hasAttribute('disabled')) return;

    this.values.push(e);
  }

  onChangeCheckbox(value: string, id: number) {
    let elementById = document.getElementById(`${id}`) as HTMLButtonElement;
    if (elementById.hasAttribute('disabled')) return;

    if (this.values.includes(value)) {
      this.values = this.values.filter((item) => item !== value);
    } else {
      this.values.push(value);
    }
  }

  calculateAnswers(question: any) {
    let values = this.currentAnswer;
    let isAllCorrect = values.every(value => value.isCorrect == true);
    if(isAllCorrect) {
      this.answersScores += question.score;
    }
  }


  ngOnInit() {
    for (const question of this.questions) {
      this.scores += Number(question.score);
    }
  }

  answerAndQuestionLengths() {
    return this.questions.length() == this.answers.length();
  }

  protected readonly String = String;
}
<div>
  <h1>{{quizTitle}}</h1>

  <hr>
  <br>

  <form #questionsForm=ngForm>
    <div class="card ml-3 mb-3" *ngFor="let question of questions; let x = index" style="width: 18rem;">
      <div class="card-body">
        <h5 class="card-title">Question {{x + 1}}. {{question.title}}</h5>
        <h6 class="card-subtitle mb-2 text-muted">score: {{question.score}}</h6>

        <ng-container *ngFor="let option of getAllOptionsOfQuestion(question); let y = index">
          <input [value]="option.title" class="form-check-inline" type="radio" name="radio_answer_{{question.id}}"
                 *ngIf="String(question.questionType) == 'SINGLE_SELECT'" (change)="onChangeRadio(option, question.id)">
          <input [value]="option.title" class="form-check-inline" type="checkbox" name="checkbox_answer_{{question.id}}"
                 *ngIf="String(question.questionType) == 'MULTI_SELECT'"
                 (change)="onChangeCheckbox(option, question.id)">

          <span class="card-text">{{y + 1}} ) {{option.title}}</span>
          <br>
        </ng-container>

        <br>
        <button [id]="question.id" class="btn btn-primary btn-success" (click)="addAnswer(question.id);">Confirm
        </button>

        <ng-container *ngIf="calculateAnswers(question)"></ng-container>
      </div>
    </div>
  </form>

  <ng-container *ngIf="answerAndQuestionLengths()">
    <p>{{answersScores}} / {{scores}}</p>
  </ng-container>

</div>
