import {AfterContentInit, Component, OnInit} from '@angular/core';
import {AxiosService} from "../../axios.service";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements AfterContentInit {
  token: string | null = null;

  constructor(private axiosService: AxiosService) {
  }

  ngAfterContentInit(): void {
    let authToken = this.axiosService.getAuthToken();
    if (authToken !== null) {
      this.token = authToken;
    } else {
      this.token = null;
    }
  }

  logout() {
    this.axiosService.setAuthToken(null);
  }


}
<div class="container" style="width: 100%; height: 55px">
  <div class="row">
    <nav class="nav nav-pills" style="position: absolute; left: 50px; top: 20px">
      <li><a class="header-nav-li-a" routerLinkActive='active' *ngIf="token != null" routerLink="/">Home</a></li>
      <li><a class="header-nav-li-a" routerLinkActive='active' *ngIf="token != null" routerLink="/quizzes-page">Quizzes</a></li>
      <li><a class="header-nav-li-a" routerLinkActive='active' *ngIf="token != null" routerLink="/quizzes-page/create-page">Create Quiz</a></li>
    </nav>

    <div class="site-logo-div">
      <h2 class="site-logo">Quizzes</h2>
    </div>

    <nav class="nav nav-pills" style="position: absolute; right: 50px; top: 20px">
      <li><a class="header-nav-li-a" routerLinkActive='active' *ngIf="token == null" routerLink="/login-page">Login</a></li>
      <li><a class="header-nav-li-a" routerLinkActive='active' *ngIf="token == null" routerLink="/register-page">Register</a></li>

      <li><a class="header-nav-li-a" routerLinkActive='active' *ngIf="token != null" href="http://localhost:8080/logout" (click)="logout()">Logout</a></li>
    </nav>
  </div>
</div>

