import {Component, Input, Output} from '@angular/core';

@Component({
  selector: 'app-quiz-card',
  templateUrl: './quiz-card.component.html',
  styleUrls: ['./quiz-card.component.css']
})
export class QuizCardComponent {

  @Input() quizTitle: string = 'asd';
  @Input() quizCreatedDateTime: any = '11/22/3333';
}

      <div class="col-md-4">
        <div class="card p-3">
          <div class="card-block">
            <img src="assets/quiz.png" alt="quiz png" width="40px">
            <h4 class="card-title pt-1">{{quizTitle}}</h4>
            <h6 class="card-subtitle text-muted pt-1">{{quizCreatedDateTime}}</h6>
            <button href="#" class="btn btn-primary btn-success mt-3">Take the quiz</button>
          </div>
        </div>
      </div>
