import {Component, EventEmitter, OnInit, Output} from '@angular/core';
import {HttpClient, HttpHeaders, HttpParams} from "@angular/common/http";
import {AxiosService} from "../../axios.service";
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent{

  @Output() onSubmitLoginEvent = new EventEmitter();

  password: string = "";
  username: string = "";

  constructor(private axiosService: AxiosService) {
  }

  onSubmitLogin(form: any){
    this.onSubmitLoginEvent.emit()
  }
}
<div class="login">

  <div class="login-form text-center">
    <h3 class="login-form-name" style="font-family: 'Raleway', sans-serif;">Login Form</h3>

    <form #loginForm=ngForm class="mt-4" (ngSubmit)="login(loginForm)">
        <div class="form-group">
          <label style="font-family: 'Raleway', sans-serif;">Email</label>
          <input type="text" name="username" placeholder="Email" class="form-control" [(ngModel)]="username"/>
        </div>

        <div class="form-group">
          <label style="font-family: 'Raleway', sans-serif;">Password</label>
          <input type="password" name="password" placeholder="Password" class="form-control" [(ngModel)]="password">
        </div>

        <div class="form-group mt-4" style="width: 100%;">
          <button type="submit" class="btn btn-success" style="width: 80px">Login</button>
        </div>
    </form>
  </div>
</div>
